import React, { useEffect, useState } from 'react';
import { getMaps, createMap, updateMap, deleteMap } from '../../services/api';
import './Home.css'; // Create a separate CSS file for styling

const Home = () => {
  const [maps, setMaps] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingMapId, setEditingMapId] = useState(null);
  const [newMapName, setNewMapName] = useState('');

  useEffect(() => {
    const fetchMaps = async () => {
      const token = localStorage.getItem('token');
      try {
        const res = await getMaps(token);
        setMaps(res.data);
        setLoading(false);
      } catch (err) {
        console.error('Failed to fetch maps:', err);
        setLoading(false);
      }
    };

    fetchMaps();
  }, []);

  const handleCreateMap = async () => {
    const token = localStorage.getItem('token');
    try {
      const newMapName = `Map ${maps.length}`;
      const res = await createMap(newMapName, token);
      setMaps([...maps, res.data]);
    } catch (err) {
      console.error('Failed to create map:', err);
    }
  };

  const handleRenameMap = async (mapId) => {
    const token = localStorage.getItem('token');
    try {
      await updateMap(mapId, newMapName, token);
      const updatedMaps = maps.map(map => map.id === mapId ? { ...map, name: newMapName } : map);
      setMaps(updatedMaps);
      setEditingMapId(null);
    } catch (err) {
      console.error('Failed to rename map:', err);
    }
  };

  const handleDeleteMap = async (mapId) => {
    const token = localStorage.getItem('token');
    try {
      await deleteMap(mapId, token);
      const updatedMaps = maps.filter(map => map.id !== mapId);
      setMaps(updatedMaps);
    } catch (err) {
      console.error('Failed to delete map:', err);
    }
  };

  return (
    <div>
      <h1>Welcome to Map-My-Major</h1>
      <button onClick={handleCreateMap}>New Map</button>
      {loading ? (
        <p>Loading maps...</p>
      ) : (
        <div className="map-grid">
          {maps.map((map) => (
            <div key={map.id} className="map-card">
              <div className="map-card-header">
                {editingMapId === map.id ? (
                  <input
                    type="text"
                    value={newMapName}
                    onChange={(e) => setNewMapName(e.target.value)}
                    onBlur={() => handleRenameMap(map.id)}
                    autoFocus
                  />
                ) : (
                  <h3 onClick={() => setEditingMapId(map.id)}>{map.name}</h3>
                )}
                <div className="options-icon" onClick={() => setEditingMapId(map.id)}>
                  <img src="/options-icon.png" alt="Options" />
                  <div className="dropdown-content">
                    <div onClick={() => setEditingMapId(map.id)}>Rename</div>
                    <div onClick={() => handleDeleteMap(map.id)}>Delete</div>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Home;
