import React, { useEffect, useState } from 'react';
import { getMaps, createMap } from '../services/api';

const Home = () => {
  const [maps, setMaps] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMaps = async () => {
      const token = localStorage.getItem('token');
      try {
        const res = await getMaps(token);
        setMaps(res.data);
        setLoading(false);
      } catch (err) {
        console.error('Failed to fetch maps:', err);
        setLoading(false);
      }
    };

    fetchMaps();
  }, []);

  const handleCreateMap = async () => {
    const token = localStorage.getItem('token');
    try {
      const newMapName = `Map ${maps.length}`;
      const res = await createMap(newMapName, token);
      setMaps([...maps, res.data]);
    } catch (err) {
      console.error('Failed to create map:', err);
    }
  };

  return (
    <div>
      <h1>Welcome to Map-My-Major</h1>
      <button onClick={handleCreateMap}>New Map</button>
      {loading ? (
        <p>Loading maps...</p>
      ) : (
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))', gap: '16px' }}>
          {maps.map((map, index) => (
            <div key={map.id} style={{ border: '1px solid #ccc', padding: '16px' }}>
              <input
                type="text"
                defaultValue={map.name}
                onBlur={(e) => {
                  // Optionally, handle saving the updated map name here
                }}
              />
              <p>Map ID: {map.id}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Home;
