import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getMap, addSemester, addClass, deleteSemester, deleteClass } from '../../services/api';
import './MapPage.css';

const MapPage = () => {
  const { mapName } = useParams();
  const [map, setMap] = useState(null);
  const [loading, setLoading] = useState(true);
  const [deleteMode, setDeleteMode] = useState(false);

  useEffect(() => {
    const fetchMap = async () => {
      const token = localStorage.getItem('token');
      try {
        const fetchedMap = await getMap(mapName, token);
        setMap(fetchedMap);
        setLoading(false);
      } catch (err) {
        console.error('Failed to fetch map:', err);
        setLoading(false);
      }
    };

    fetchMap();
  }, [mapName]);

  const handleAddSemester = async () => {
    const token = localStorage.getItem('token');
    try {
      const res = await addSemester(map.id, token);
      setMap(prevMap => ({
        ...prevMap,
        semesters: [...prevMap.semesters, res.data]
      }));
    } catch (err) {
      console.error('Failed to add semester:', err);
    }
  };
  

  const handleAddClass = async (semesterId) => {
    const token = localStorage.getItem('token');
    const className = prompt('Enter class name:');
    if (className) {
      try {
        const res = await addClass(semesterId, className, token);
        setMap(prevMap => ({
          ...prevMap,
          semesters: prevMap.semesters.map(sem =>
            sem.id === semesterId ? { ...sem, classes: [...sem.classes, res.data] } : sem
          ),
        }));
      } catch (err) {
        console.error('Failed to add class:', err);
      }
    }
  };

  const handleDeleteSemester = async (semesterId) => {
    const token = localStorage.getItem('token');
    try {
      await deleteSemester(semesterId, token);
      setMap(prevMap => ({
        ...prevMap,
        semesters: prevMap.semesters.filter(sem => sem.id !== semesterId)
      }));
    } catch (err) {
      console.error('Failed to delete semester:', err);
    }
  };

  const handleDeleteClass = async (classId) => {
    const token = localStorage.getItem('token');
    try {
      await deleteClass(classId, token);
      setMap(prevMap => ({
        ...prevMap,
        semesters: prevMap.semesters.map(sem => ({
          ...sem,
          classes: sem.classes.filter(cls => cls.id !== classId),
        })),
      }));
    } catch (err) {
      console.error('Failed to delete class:', err);
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!map) {
    return <div>Map not found</div>;
  }

  return (
    <div>
      <h1>{mapName}</h1>
      <div className="map-container">
        {map.semesters.map((semester, index) => (
          <div key={semester.id} className="semester-column">
            <h3>Semester {index + 1}</h3>
            {deleteMode && (
              <button onClick={() => handleDeleteSemester(semester.id)}>Delete Semester</button>
            )}
            {semester.classes.map(classObj => (
              <div key={classObj.id} className="class-card">
                {classObj.name}
                {deleteMode && (
                  <button onClick={() => handleDeleteClass(classObj.id)}>Delete</button>
                )}
              </div>
            ))}
            <button onClick={() => handleAddClass(semester.id)}>Add Class</button>
          </div>
        ))}
      </div>
      <button onClick={handleAddSemester}>Add Semester</button>
      <button onClick={() => setDeleteMode(!deleteMode)}>
        {deleteMode ? 'Exit Delete Mode' : 'Delete Mode'}
      </button>
    </div>
  );
};

export default MapPage;
